mapper.select:
select comment.* ,build.building , user.userpic as userpic,favor.userid as fuserid,favor.favortype,user.uname as uname from comment left join user on(comment.id=user.userid) left join build on( build.buildingid=comment.buildingid) left join favor on(comment.commentid=favor.commentid)
idtype=1
and comment.buildingid=#{buildingid}
and comment.idtype = #{idtype}
and comment.comtype like "%" #{comtype} "%"
UNION select comment.* ,build.building,cta.ctaimg as userpic ,favor.userid as fuserid,favor.favortype,cta.ctaname as uname from comment left join cta on(comment.id=cta.ctaid) left join build on( build.buildingid=comment.buildingid) left join favor on(comment.commentid=favor.commentid)
idtype =3
and comment.buildingid=#{buildingid}
and comment.idtype= #{idtype}
and comment.comtype like "%" #{comtype} "%"
UNION select comment.* ,build.building ,company.compic as userpic,favor.userid as fuserid,favor.favortype,company.comuname as uname from comment left join company on(comment.id=company.comid) left join build on( build.buildingid=comment.buildingid) left join favor on(comment.commentid=favor.commentid)
idtype =2
and comment.buildingid=#{buildingid}
and comment.idtype= #{idtype}
and comment.comtype like "%" #{comtype} "%"
ORDER BY comtime DESC
